package Basics;

public class LogicalNOT {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10, b = 1;
		 
        // Displaying a, b, c
        System.out.println("Var1 = " + a);
        System.out.println("Var2 = " + b);
 
        // Using logical NOT operator
        System.out.println("!(a < b) = " + !(a < b));
        System.out.println("!(a > b) = " + !(a > b));

	}

}
